
public class ExampleOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a = false;
		boolean b = true;
		
		if(a) {
			System.out.println("Inside if");
		}
		else if(b) {
			System.out.println("Inside else-if");
		}
		else {
			System.out.println("Inside else");
		}
	}

}
