
public class ExampleOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int value = 7;
			
			switch(value) {
			
			case 4: 
				System.out.println("Case 4");
				break;
			case 5:
				System.out.println("Case 5");
				break;
			case 6:
				System.out.println("Case 6");
				break;
			default:
				System.out.println("Default Case");
			}
	}

}
